class Solution:

    def longestValidParentheses(self, s):
        """
        :type s: str
        :rtype: int
        """
        ss = []
        res = []
        for s_idx, x in enumerate(s):
            if x == ')':
                if not ss:
                    continue
                ss_last = ss.pop(-1)
                replace = False
                if s[ss_last] == '(':
                    if res:
                        for idx, (t_s, t_e) in enumerate(res):
                            if t_e + 1 == ss_last:
                                res.pop(idx)
                                res.append((t_s, s_idx))
                                replace = True
                    if not replace:
                        res.append((ss_last, s_idx))
                else:
                    ss = []
            else:
                ss.append(s_idx)

        max_len = 0
        for a, b in res:
            if b-a + 1> max_len:
                max_len = b - a + 1
        return max_len
